package test;

public class Strifunc {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="keert hana";
		String b="sai";
		System.out.println("original string "+a);
		System.out.println("lower case "+a.toLowerCase());
		System.out.println("upper case "+a.toUpperCase());
		System.out.println("replace "+a.replace('e','s'));
		System.out.println("trim "+a.trim());
		System.out.println("length "+a.length());
		System.out.println("equals "+a.equals(a));
		System.out.println("character "+a.charAt(3));
		System.out.println("concat "+a.concat(b));
		System.out.println("substring "+a.substring(2));
		System.out.println("substring "+a.substring(2,4));
		

	}

}
